      <main className="main">
        <Switch>
          <Route exact path="/">
            <Home darkTheme={darkTheme} />
          </Route>
          <Route path="/about">
            <About darkTheme={darkTheme} />
          </Route>
          <Route path="/projects">
            <Projects darkTheme={darkTheme} />
          </Route>
        </Switch>
      </main>
    </>
  );
};

export default App;
