import React, { useState } from "react";
import data from "../data.json";
import "../styles/project.scss";
import Card from "./Card";

const Projects = ({ theme }) => {
  const [productImage, grabProjectImage] = useState("");

  return (
    <section className={`projects ${theme}-mode`}>
      <br />
      <br />

      <div className="cards grid">
        <div className="preview">{productImage ? productImage : ""}</div>
        <div className="list-container">
          <header>
            <h1>Projects</h1>
            <hr style={{ "border-color": " var(--orange" }} />
          </header>

          <div className="grid lists">
            <br />
            <br />
            <br />
            <br />
            <br />
            {data.projects.map((project, i) => {
              return (
                <>
                  <Card
                    grabProjectImage={grabProjectImage}
                    key={i}
                    project={project}
                  />
                </>
              );
            })}
            <br />
            <br />
            <br />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
